mapper.select:
SELECT 

FROM xf_alarm a

WHERE a.id = #{id}

SELECT 

FROM xf_alarm a

where a.del_flag = #{DEL_FLAG_NORMAL}

	and a.ieme=#{ieme}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM xf_alarm a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE xf_alarm SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
