mapper.select:
SELECT 

FROM xf_device_check a

WHERE a.id = #{id}

SELECT 

FROM xf_device_check a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM xf_device_check a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE xf_device_check SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
