mapper.select:
SELECT 

FROM xf_device a

WHERE a.id = #{id}

SELECT 

FROM xf_device a


	a.del_flag = #{DEL_FLAG_NORMAL}

	 and a.create_date between #{startDate} and #{endDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM xf_device a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE xf_device SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
