mapper.select:
SELECT 

FROM sys_building a

WHERE a.id = #{id}

SELECT 

FROM sys_building a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		and a.building_no = #{buildingNo}


		and a.floor_num = #{floorNum}


		and a.ieme = #{ieme}



		and a.address = #{address}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sys_building a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_building SET 	
	status = #{status},
	alarm_date = #{alarmDate}

WHERE ieme = #{ieme}

